mapper.select:
SELECT PARCEL_NUMBER, PARCEL_DATE, PARCEL_STATUS, PARCEL_INVOICE, PARCEL_COMPANY, DELIVERY_ADDRESS1, DELIVERY_ADDRESS2, DELIVERY_POSTCODE, PAYMENT_PRICE, PAYMENT_STATUS, PRODUCT_NAME, PRODUCT_OPTION, USER_NAME, USER_PHONE_NUMBER FROM( SELECT ROWNUM AS RNUM, PARCEL_NUMBER, PARCEL_DATE, PARCEL_STATUS, PARCEL_INVOICE, PARCEL_COMPANY, DELIVERY_ADDRESS1, DELIVERY_ADDRESS2, DELIVERY_POSTCODE, PAYMENT_PRICE, PAYMENT_STATUS, PRODUCT_NAME, PRODUCT_OPTION, USER_NAME, USER_PHONE_NUMBER
FROM( SELECT PARCEL_NUMBER, PARCEL_DATE, PARCEL_STATUS, PARCEL_INVOICE, PARCEL_COMPANY, DELIVERY_ADDRESS1, DELIVERY_ADDRESS2, DELIVERY_POSTCODE, PAYMENT_PRICE, PAYMENT_STATUS, PRODUCT_NAME, PRODUCT_OPTION, USER_NAME, USER_PHONE_NUMBER FROM MONTHLY_PARCEL P LEFT JOIN MONTHLY_PAYMENT PM on P.PAYMENT_NUMBER = PM.PAYMENT_NUMBER LEFT JOIN MONTHLY_PRODUCT PR ON PM.PRODUCT_NUMBER = PR.PRODUCT_NUMBER AND BRAND_NUMBER = #{brandNumber} LEFT JOIN MONTHLY_USER U on PM.USER_NUMBER = U.USER_NUMBER
PARCEL_NUMBER = #{searchVo.searchInput}
USER_NAME LIKE '%' || #{searchVo.searchInput} || '%'
PRODUCT_NAME LIKE '%' || #{searchVo.searchInput} || '%'
AND TRUNC(PARCEL_DATE) = TRUNC(SYSDATE)
AND TRUNC(PARCEL_DATE) = TRUNC(SYSDATE)-1
AND P.PARCEL_DATE >= TRUNC(SYSDATE) - INTERVAL '1' MONTH
AND P.PARCEL_DATE >= TRUNC(SYSDATE) - INTERVAL '3' MONTH
AND PARCEL_STATUS = TO_NUMBER(#{searchVo.productStatus})
ORDER BY PARCEL_DATE) S1
WHERE ROWNUM <= #{criteria.page}*#{criteria.amount}
)S2 WHERE RNUM > (#{criteria.page}-1)*#{criteria.amount}

mapper.update:
UPDATE MONTHLY_PARCEL
SET PARCEL_STATUS = #{parcelStatus},
    PARCEL_COMPANY = #{parcelCompany},
    PARCEL_INVOICE = #{parcelInvoice}
WHERE PARCEL_NUMBER = #{parcelNumber}
